import { useState, useEffect, useRef } from "react";
import TimerText from "./TimerText";
import TimerButton from "./TimerButton";
import "./../App.css";

function Timer() {
    const [timer, setTimer] = useState(null);
    let timeLeft = useRef(1500);
    let minutes = useRef(25);
    let seconds = useRef(0);

    function UpdateTime() {
        minutes.current = Math.floor(timeLeft.current / 60);
        seconds.current = timeLeft.current % 60;
    }

    function StartTimer() {
        // prevent creation of timer multiple times
        if(timer) {
            clearInterval(timer);
        }

        const id = setInterval(() => {
            UpdateTime();
        }, 1000);

        setTimer(id);
    }


    function StopTimer() {
        clearInterval(timer);
        setTimer(null);
    }

    useEffect(() => {
        StartTimer();
        return () => {
            if(timer) {
                clearInterval(timer);
            }
        }
    }, [timer]);

    return (
        <div>
            <TimerText minutes={minutes} seconds={seconds}/>
            <TimerButton onClick={StartTimer}/>
        </div>
    );
}

export default Timer;
