import { useState, useEffect, useRef } from "react";
import Timer from "./components/Timer";
import Settings from "./components/Settings";
import AppHeader from "./components/AppHeader";
import { PAGES, VISIBILITY } from "./utils/Constants";
import "./App.css";

function App() {
  const [currPage, setCurrPage] = useState(PAGES.TIMER);

  // page element references 
  const timerElement = useRef(null);
  const settingsElement = useRef(null);
  const [pageVisibilities] = useState(new Map());

  function switchPages(page) {
    if(currPage == page) return;

    setCurrPage(page);
  }

  function onStartup() {
    console.log("hi");
    pageVisibilities.set(PAGES.TIMER, true);
    pageVisibilities.set(PAGES.SETTINGS, false);
  }

  useEffect(() => {
    onStartup();
  }, []);

  return (
    <div className="App">
      <AppHeader onClick={switchPages}/>
      <div className="Timer-background">
        <Timer visible={pageVisibilities.get(PAGES.TIMER)}/>
        <Settings visible={pageVisibilities.get(PAGES.SETTINGS)}/>
      </div>
    </div>
  );
}

export default App;
