import { useState, useEffect, useRef } from "react";
import TimerText from "./TimerText";
import TimerButton from "./TimerButton";
import "./../App.css";

function Timer() {
    // total time in seconds
    // initial time is 25 minutes
    const[time, setTime] = useState(1500);

    const [seconds, setSeconds] = useState(0);
    const [minutes, setMinutes] = useState(25);

    const [isRunning, setIsRunning] = useState(true);

    const firstIsRunning = useRef(true);
    const tick = useRef();


    function UpdateTime() {
        setTime(time - 1);
        setMinutes(Math.floor(time / 60))
        setSeconds(time % 60);
    }

    function ToggleIsRunning() {
        setIsRunning(!isRunning);
    }

    useEffect(() => {
        if(firstIsRunning.current) {
            firstIsRunning.current = !firstIsRunning.current;
            return;
        }

        if(isRunning) {
            tick.current = setInterval(() => {
                UpdateTime();
            }, 1000);
        }
        else {
            clearInterval(tick.current);
        }

        return () => clearInterval(tick.current);
    }, [isRunning]);

    return (
        <div>
            <TimerText minutes={minutes} seconds={seconds}/>
            <TimerButton onClick={ToggleIsRunning}/>
        </div>
    );
}

export default Timer;
