import { useState, useEffect } from "react";
import TimerText from "./TimerText";
import TimerButton from "./TimerButton";
import "./../App.css";

function Timer() {
    // total time in seconds
    // initial time is 25 minutes
    const[time, setTime] = useState(1500);

    const [seconds, setSeconds] = useState(0);
    const [minutes, setMinutes] = useState(25);

    // state to store if timer should countdown
    const [isRunning, setIsRunning] = useState(true);

    let interval = null;

    function UpdateTime() {
        // subtract a second from time
        setTime(time => time - 1);

        // now calculate the time in minutes and remaining seconds
        setMinutes(Math.floor(time / 60))
        setSeconds(time % 60);
    }

    function ToggleIsRunning() {
        if(isRunning) {
            setIsRunning(false);
        }
        else {
            setIsRunning(true);
        }
        console.log("isRunning: " + isRunning);
    }

    // start timer
    // updates every 1 second
    useEffect(() => {
        // set interval
        interval = setInterval(() => { 
            if(isRunning) {
                UpdateTime() 
            }
        }, 1000);

        // clear interval
        return () => clearInterval(interval);
    }, [time, seconds, minutes]);

    return (
        <div>
            <TimerText minutes={minutes} seconds={seconds}/>
            <TimerButton onClick={ToggleIsRunning}/>
        </div>
    );
}

export default Timer;
